import actions as act, timelast_price = 0while 1:    time.sleep(900)        price = act.fetch_price('AAPL')    mv_avg = act.fetch_moving_average('AAPL')        if (last_price <= mv_avg and price >= mv_avg) and last_price > 0:        pos = act.fetch_position('AAPL')        if len(pos) == 0:            act.place_order('AAPL', 100, 'buy', 'market', 'day')            elif (last_price >= mv_avg and price <= mv_avg) and last_price > 0:        pos = act.fetch_position('AAPL')        if len(pos) != 0:            act.place_order('AAPL', 100, 'sell', 'market', 'day')                last_price = price    